import React, { useEffect, useState } from 'react';
import ReactDOM  from 'react-dom/client';
import { resturantlist } from './Config';
import ResturantCard from './ResturantCard';
import Shimmer from './Shimmer';

function filterdata(searchtxt, searchresturants){
     return (searchresturants.filter((resturant) => resturant.info.name.includes(searchtxt)));
}

  const Body =() =>{
//  let searchtxt = "hello" javascript local variable 
const [searchtxt, setSearchtxt] = useState();
const [searchresturants, setsearchresturants] = useState(resturantlist);
// const [toggel, settoggel] = useState("false");

useEffect(()=>{
    getResturants();
}, []);

async function getResturants() {
    const data = await fetch("https://www.swiggy.com/dapi/restaurants/list/v5?lat=23.179683924526447&lng=79.93636887520552&offset=15&sortBy=RELEVANCE&pageType=SEE_ALL&page_type=DESKTOP_SEE_ALL_LISTING");
    const json = await data.json();
    console.log(json);
    setsearchresturants(json?.data?.cards); 
}
    return (searchresturants.length === 0)?<Shimmer/>:(
        <>
        <div className='search-container'>
            <input type='text' className='search-input' placeholder='Search' value={searchtxt} onChange={(e) => {setSearchtxt(e.target.value)}}/>
            <button className='search-btn' onClick={() => {
               const data =  filterdata(searchtxt, searchresturants);
               setsearchresturants(data);
            }}>Search</button>  

            {/* <h1>{toggel}</h1>
            <button onClick={() => {
                if(toggel === "false")
                settoggel("true");
                else
                settoggel("false");
            }}> change </button> */}
        </div>
    <div className='resturant-list'>
      {searchresturants.map((resturant) => {
        return <ResturantCard {...resturant.info} key = {resturant.info.id}/>
      })}
    </div></>
    )
  }

  export default Body;
